class HumanDimensions:
    def __init__(
        self,
        stature=None,
        chest_circumference=None,
        waist_circumference=None,
        pelvis_circumference=None,
    ):
        self.stature = stature
        self.chest_circumference = chest_circumference
        self.waist_circumference = waist_circumference
        self.pelvis_circumference = pelvis_circumference
